    def run_engine(self):
        ho = self.ho.isChecked()
        khac_dom = self.khac_dom.isChecked()
        kho_tho = self.kho_tho.isChecked()
        tuoi_tren_40 = self.kho_tho_2.isChecked()
        hut_thuoc = self.tuoi_tren_40.isChecked()
        
        engine = COPDExpertSystem()
        engine.reset()
        engine.declare(PatientData(ho=ho, khac_dom=khac_dom, kho_tho=kho_tho, tuoi_tren_40=tuoi_tren_40, hut_thuoc=hut_thuoc))
        engine.run()

        for fact in engine.facts.values():
            if isinstance(fact, Fact) and fact.get("screening") == "positive":
                self.textEdit.setText("Kết quả: Có nguy cơ BPTNMT. Khuyến cáo đo chức năng hô hấp.")
                return
        self.textEdit.setText("Kết quả: Không có nguy cơ BPTNMT.")

